import React, { useState } from 'react';
import '../style/Category.css'

function Categories({ categories, activateCategory }) {
  console.log(categories);
  const [activeCateg, setActiveCateg] = useState('');
  
  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setActiveCateg(selectedCategory);
    activateCategory(selectedCategory);
  };

  const handleReset = () => {
    setActiveCateg('');
    activateCategory('');
  };

  return (
    <div className='jh-categorie'>
      <select 
        value={activeCateg}
        className='jh-categorie-select'
        onChange={handleCategoryChange}
      >
        <option value={''}>none</option>
        {categories.map((category) => (
          <option key={category} value={category}>{category}</option>
        ))}
      </select>
      <button onClick={handleReset}>reset</button>
    </div>
  );
}

export default Categories;
